import React, { useState, useEffect } from "react";
import API from "../api.js";
import "./Dashboard.css";

export default function Dashboard() {
  const [meals, setMeals] = useState([]);
  const [form, setForm] = useState({ meal_name: "", total_calories: "", notes: "" });
  const user_id = 1; // replace with actual logged-in user ID

  async function fetchMeals() {
    const res = await API.get(`/meals/${user_id}`);
    setMeals(res.data);
  }

  useEffect(() => {
    fetchMeals();
  }, []);

  async function handleAdd(e) {
    e.preventDefault();
    await API.post("/meals", { ...form, user_id });
    setForm({ meal_name: "", total_calories: "", notes: "" });
    fetchMeals();
  }

  async function handleUpdate(id) {
    const newName = prompt("Update meal name:");
    const newCalories = prompt("Update calories:");
    if (newName && newCalories) {
      await API.put(`/meals/${id}`, {
        meal_name: newName,
        total_calories: newCalories,
        notes: "Updated",
      });
      fetchMeals();
    }
  }

  async function handleDelete(id) {
    if (window.confirm("Delete this meal?")) {
      await API.delete(`/meals/${id}`);
      fetchMeals();
    }
  }

  const totalCalories = meals.reduce((sum, m) => sum + Number(m.total_calories || 0), 0);

  return (
    <div className="dashboard">
      <h1>ü•ó Nutrition Dashboard</h1>

      <form className="meal-form" onSubmit={handleAdd}>
        <input
          type="text"
          placeholder="Meal name"
          value={form.meal_name}
          onChange={(e) => setForm({ ...form, meal_name: e.target.value })}
          required
        />
        <input
          type="number"
          placeholder="Calories"
          value={form.total_calories}
          onChange={(e) => setForm({ ...form, total_calories: e.target.value })}
          required
        />
        <input
          type="text"
          placeholder="Notes"
          value={form.notes}
          onChange={(e) => setForm({ ...form, notes: e.target.value })}
        />
        <button type="submit">‚ûï Add Meal</button>
      </form>

      <table>
        <thead>
          <tr>
            <th>Meal</th>
            <th>Calories</th>
            <th>Notes</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {meals.map((m) => (
            <tr key={m.meal_id}>
              <td>{m.meal_name}</td>
              <td>{m.total_calories}</td>
              <td>{m.notes}</td>
              <td>
                <button onClick={() => handleUpdate(m.meal_id)}>‚úèÔ∏è</button>
                <button onClick={() => handleDelete(m.meal_id)}>üóëÔ∏è</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <div className="summary">
        <h3>üìä Nutrition Summary</h3>
        <p>Total Meals: {meals.length}</p>
        <p>Total Calories: {totalCalories} kcal</p>
      </div>
    </div>
  );
}
