export default function Dashboard() {
  const [meals, setMeals] = useState([]);
  const [form, setForm] = useState({
    meal_name: "",
    total_calories: "",
    protein: "",
    carbs: "",
    fat: "",
    notes: "",
  });
  const user_id = 1; // temporary until login logic connects

  async function fetchMeals() {
    try {
      const res = await API.get(`/meals/${user_id}`);
      setMeals(res.data);
    } catch (err) {
      console.error("‚ùå Fetch error:", err);
    }
  }

  useEffect(() => {
    fetchMeals();
  }, []);

  async function handleAdd(e) {
    e.preventDefault();
    try {
      await API.post("/meals", { ...form, user_id });
      setForm({
        meal_name: "",
        total_calories: "",
        protein: "",
        carbs: "",
        fat: "",
        notes: "",
      });
      fetchMeals();
    } catch (err) {
      console.error("‚ùå Add meal failed:", err);
    }
  }

  async function handleUpdate(id) {
    const newName = prompt("Update meal name:");
    const newCalories = prompt("Update calories:");
    const newProtein = prompt("Update protein (g):");
    const newCarbs = prompt("Update carbs (g):");
    const newFat = prompt("Update fat (g):");

    if (newName && newCalories) {
      await API.put(`/meals/${id}`, {
        meal_name: newName,
        total_calories: newCalories,
        protein: newProtein || 0,
        carbs: newCarbs || 0,
        fat: newFat || 0,
        notes: "Updated meal info",
      });
      fetchMeals();
    }
  }

  async function handleDelete(id) {
    if (window.confirm("Delete this meal?")) {
      await API.delete(`/meals/${id}`);
      fetchMeals();
    }
  }

  const totalCalories = meals.reduce((sum, m) => sum + Number(m.total_calories || 0), 0);
  const totalProtein = meals.reduce((sum, m) => sum + Number(m.protein || 0), 0);
  const totalCarbs = meals.reduce((sum, m) => sum + Number(m.carbs || 0), 0);
  const totalFat = meals.reduce((sum, m) => sum + Number(m.fat || 0), 0);

  return (
    <div className="dashboard">
      <h1>ü•ó Nutrition Dashboard</h1>

      <form className="meal-form" onSubmit={handleAdd}>
        <input
          type="text"
          placeholder="Meal name"
          value={form.meal_name}
          onChange={(e) => setForm({ ...form, meal_name: e.target.value })}
          required
        />
        <input
          type="number"
          placeholder="Calories"
          value={form.total_calories}
          onChange={(e) => setForm({ ...form, total_calories: e.target.value })}
          required
        />
        <input
          type="number"
          placeholder="Protein (g)"
          value={form.protein}
          onChange={(e) => setForm({ ...form, protein: e.target.value })}
        />
        <input
          type="number"
          placeholder="Carbs (g)"
          value={form.carbs}
          onChange={(e) => setForm({ ...form, carbs: e.target.value })}
        />
        <input
          type="number"
          placeholder="Fat (g)"
          value={form.fat}
          onChange={(e) => setForm({ ...form, fat: e.target.value })}
        />
        <input
          type="text"
          placeholder="Notes"
          value={form.notes}
          onChange={(e) => setForm({ ...form, notes: e.target.value })}
        />
        <button type="submit">‚ûï Add Meal</button>
      </form>

      <table>
        <thead>
          <tr>
            <th>Meal</th>
            <th>Calories</th>
            <th>Protein (g)</th>
            <th>Carbs (g)</th>
            <th>Fat (g)</th>
            <th>Notes</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {meals.map((m) => (
            <tr key={m.meal_id}>
              <td>{m.meal_name}</td>
              <td>{m.total_calories}</td>
              <td>{m.protein}</td>
              <td>{m.carbs}</td>
              <td>{m.fat}</td>
              <td>{m.notes}</td>
              <td>
                <button onClick={() => handleUpdate(m.meal_id)}>‚úèÔ∏è</button>
                <button onClick={() => handleDelete(m.meal_id)}>üóëÔ∏è</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <div className="summary">
        <h3>üìä Nutrition Summary</h3>
        <p>Total Meals: {meals.length}</p>
        <p>Total Calories: {totalCalories} kcal</p>
        <p>Protein: {totalProtein} g</p>
        <p>Carbs: {totalCarbs} g</p>
        <p>Fat: {totalFat} g</p>
      </div>
    </div>
  );
}
