import React, { useState } from 'react';
import API from '../api';
import { useNavigate } from 'react-router-dom';

export default function Signup(){
  const [form,setForm] = useState({name:'',email:'',password:'',age:'',weight:'',height:'',activity_level:'Medium',goal:'Maintain Weight'});
  const nav = useNavigate();
  async function submit(e){ e.preventDefault(); await API.post('/users/signup', form); nav('/login'); }
  return (
    <form className="p-6 max-w-md mx-auto" onSubmit={submit}>
      <h2 className="text-xl mb-4">Signup</h2>
      {Object.keys(form).map(k => (
        k!=='password' ? (
        <div key={k} className="mb-2">
          <label className="block text-sm">{k}</label>
          <input value={form[k]} onChange={e=>setForm({...form,[k]:e.target.value})} className="w-full p-2 border" />
        </div>) : (
        <div key={k} className="mb-2">
          <label className="block text-sm">Password</label>
          <input type="password" value={form.password} onChange={e=>setForm({...form,password:e.target.value})} className="w-full p-2 border" />
        </div>
        )
      ))}
      <button className="mt-3 p-2 bg-blue-500 text-white">Create</button>
    </form>
  );
}
