import React, { useState } from 'react';
import API from '../api';
import { saveToken } from '../auth';
import { useNavigate } from 'react-router-dom';

export default function Login(){
  const [form,setForm] = useState({email:'', password:''});
  const nav = useNavigate();
  async function submit(e){ e.preventDefault(); const res = await API.post('/users/login', form); saveToken(res.data.token); nav('/'); }
  return (
    <form onSubmit={submit} className="p-6 max-w-md mx-auto">
      <h2 className="text-xl mb-4">Login</h2>
      <input placeholder="email" value={form.email} onChange={e=>setForm({...form,email:e.target.value})} className="w-full p-2 mb-2 border" />
      <input placeholder="password" type="password" value={form.password} onChange={e=>setForm({...form,password:e.target.value})} className="w-full p-2 mb-2 border" />
      <button className="p-2 bg-green-600 text-white">Login</button>
    </form>
  );
}
